    count =0;
    for(int row = 0 ; row < 32 ; row ++){
                // int check = row;
        for(int col = 0 ; col < 64 ; col++){

            count+=4;
            buffer[2][row][col]=0x00;
            buffer[1][row][col]=0x00;
            buffer[0][row][col]=0x00;

            if(row<24 && col>28 && col <35){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
            if(row==24 && col>28 && col < 40){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
            if(row==25 && col>28 && col < 38){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
            if(row==26 && col>28 && col < 36){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
            if(row==27 && col>28 && col < 34){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
            if(row==28 && col>28 && col < 32){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
            if(row==29 && col>28 && col < 30){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
            if(row==30 && col>28 && col < 29){
                buffer[2][row][col]=0xFF;
                buffer[1][row][col]=0xFF;
                buffer[0][row][col]=0xFF;
            }
        }
        // vTaskDelay(10/portTICK_PERIOD_MS);
        ESP_LOGI(TAG,"%d",count);
    }

    while (1)
    {
        for(int row = 0 ; row < 16; row ++){
            CHOOSE_ROW(row);
            TURN_OFF_LED;

            for(int col =0 ; col < 64; col ++){

                for(int bit = 0 ; bit < 8 ; bit ++){

                    // set 1 bit color
                    for(int fre = 0 ; fre < 4 ; fre ++){
                        //  make clock bit 0 or 1
                        ((buffer[0][row][col]>> bit) & 0x01 ) ? ((fre>1)?R1_SET(1):R1_SET(0)) : ((fre>3)? R1_SET(1):R1_SET(0));
                        ((buffer[1][row][col] >> bit) & 0x01 ) ? ((fre>1)?G1_SET(1):G1_SET(0)) : ((fre>3)? G1_SET(1):G1_SET(0));
                        ((buffer[2][row][col] >> bit) & 0x01 ) ? ((fre>1)?B1_SET(1):B1_SET(0)) : ((fre>3)? B1_SET(1):B1_SET(0));
                        ((buffer[0][row+16][col]>> bit) & 0x01 ) ? ((fre>1)?R2_SET(1):R2_SET(0)) : ((fre>3)? R2_SET(1):R2_SET(0));
                        ((buffer[1][row+16][col] >> bit) & 0x01 ) ? ((fre>1)?G2_SET(1):G2_SET(0)) : ((fre>3)? G2_SET(1):G2_SET(0));
                        ((buffer[2][row+16][col] >> bit) & 0x01 ) ? ((fre>1)?B2_SET(1):B2_SET(0)) : ((fre>3)? B2_SET(1):B2_SET(0));
                    }   
                }
                MAKE_CLOCK(GPIO_NUM_25);
                
            }

            LATCH;
            TURN_ON_LED;
            vTaskDelay(1/portTICK_PERIOD_MS);
        }
    }
